mapper.insert:
insert into p2p_release_code (id, user_id, code, 
  state, create_time, modify_time
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{code,jdbcType=VARCHAR}, 
  #{state,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select
a.user_id userId, a.create_time createTime, count(1) num, b.phone phone, b.realname realName
from p2p_release_code a left join p2p_user b on a.user_id=b.user_id


		and create_time  >=  #{startDate}


		and create_time  <=  #{endDate}


		and id  <  #{id}


group by a.user_id, a.create_time
order by a.id desc

	limit #{limit}
