mapper.select:
   select 
		
   from p2p_sell
   
   	state  =  0 and match_money  >  0 and user_id  <>  #{userId,jdbcType=BIGINT}
   	 and id  >  #{id,jdbcType=BIGINT}
   
   order by id
   limit 10

select count(1)
from p2p_sell  where user_id = #{userId,jdbcType=BIGINT}
 and TO_DAYS(create_time) = TO_DAYS(#{nowDate})

select count(1)
from p2p_sell
where user_id = #{userId,jdbcType=BIGINT} and state  <  #{state,jdbcType=INTEGER}
